mapper.select:
select
contract_id, name, customer_id, business_id, check_status, examine_record_id, order_date, create_user_id,
owner_user_id, create_time, update_time, num, start_time, end_time, money, discount_rate, total_price, types,
payment_type, batch_id, ro_user_id, rw_user_id, contacts_id, remark, company_user_id, last_time, received_money,
unreceived_money, old_contract_id, money_account, address, customized, send_dept_id, send_dept_name,
send_warehouse_id, send_warehouse_name, received_status, send_notice, take_goods_type, express_company,
contract_type, freight_money, owner_user_moblie, owner_user_organize, owner_user_dept, logistics_status,
logistics_company, logistics_address, logistics_phone, send_style, purchase_status, machining_status,
machining_address, machining_remak, collection_style, collection_money, refund_money, notice_delivery,
receiving_name, receiving_mobile, receiving_address, receiving_detail_address, deal_time, kee_contract_id,
kee_contract_status, receiving_address_kd, express_company_number, customer_address_id, send_warehouse_num,
kee_actual_freight, trans_status, deliver_status, freight_fee, distribution_fee, sum_refund_money,
sum_returns_money
from wk_crm_contract


        and contract_id = #{contractId}


        and name = #{name}


        and customer_id = #{customerId}


        and business_id = #{businessId}


        and check_status = #{checkStatus}


        and examine_record_id = #{examineRecordId}


        and order_date = #{orderDate}


        and create_user_id = #{createUserId}


        and owner_user_id = #{ownerUserId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and num = #{num}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and money = #{money}


        and discount_rate = #{discountRate}


        and total_price = #{totalPrice}


        and types = #{types}


        and payment_type = #{paymentType}


        and batch_id = #{batchId}


        and ro_user_id = #{roUserId}


        and rw_user_id = #{rwUserId}


        and contacts_id = #{contactsId}


        and remark = #{remark}


        and company_user_id = #{companyUserId}


        and last_time = #{lastTime}


        and received_money = #{receivedMoney}


        and unreceived_money = #{unreceivedMoney}


        and old_contract_id = #{oldContractId}


        and money_account = #{moneyAccount}


        and address = #{address}


        and customized = #{customized}


        and send_dept_id = #{sendDeptId}


        and send_dept_name = #{sendDeptName}


        and send_warehouse_id = #{sendWarehouseId}


        and send_warehouse_name = #{sendWarehouseName}


        and received_status = #{receivedStatus}


        and send_notice = #{sendNotice}


        and take_goods_type = #{takeGoodsType}


        and express_company = #{expressCompany}


        and contract_type = #{contractType}


        and freight_money = #{freightMoney}


        and owner_user_moblie = #{ownerUserMoblie}


        and owner_user_organize = #{ownerUserOrganize}


        and owner_user_dept = #{ownerUserDept}


        and logistics_status = #{logisticsStatus}


        and logistics_company = #{logisticsCompany}


        and logistics_address = #{logisticsAddress}


        and logistics_phone = #{logisticsPhone}


        and send_style = #{sendStyle}


        and purchase_status = #{purchaseStatus}


        and machining_status = #{machiningStatus}


        and machining_address = #{machiningAddress}


        and machining_remak = #{machiningRemak}


        and collection_style = #{collectionStyle}


        and collection_money = #{collectionMoney}


        and refund_money = #{refundMoney}


        and notice_delivery = #{noticeDelivery}


        and receiving_name = #{receivingName}


        and receiving_mobile = #{receivingMobile}


        and receiving_address = #{receivingAddress}


        and receiving_detail_address = #{receivingDetailAddress}


        and deal_time = #{dealTime}


        and kee_contract_id = #{keeContractId}


        and kee_contract_status = #{keeContractStatus}


        and receiving_address_kd = #{receivingAddressKd}


        and express_company_number = #{expressCompanyNumber}


        and customer_address_id = #{customerAddressId}


        and send_warehouse_num = #{sendWarehouseNum}


        and kee_actual_freight = #{keeActualFreight}


        and trans_status = #{transStatus}


        and deliver_status = #{deliverStatus}


        and freight_fee = #{freightFee}


        and distribution_fee = #{distributionFee}


        and sum_refund_money = #{sumRefundMoney}


        and sum_returns_money = #{sumReturnsMoney}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from wk_crm_contract


        and contract_id = #{contractId}


